import React, { Component } from 'react';
import NavBar from './NavBar';
import Routes from './Routes';
import './App.css';
import axios from 'axios';

class App extends Component {
	state = {
		tweets: [
			{
				username: 'Emin Muratoglu',
				text:
					'Ssumenda quia animi mollitia laboriosam non ea eveniet ducimus repellat totam dignissimos corporis alias, veniam sapiente dolor eius maiores veritatis hic beatae. ',
				date: '10-05-20',
				avatar_src: '',
				likes: 15,
				retweets: 8
			}
		],
		api_data: ''
	};

	componentDidMount() {
		this.getTweetData();
	}

	getTweetData = async () => {
		// const res = await axios.get(`https://api.twitter.com/1.1/search/tweets.json?q=nasa&result_type=popular`);
		const res = await axios.get(`https://swapi.dev/api/people/1`);
		this.setState({ api_data: res });
	};
	render() {
		return (
			<div className="App">
				<NavBar />
				<div className="content__container">
					<h1 className="display-3">Twiter Showcase App</h1>
					<Routes tweets={this.state.tweets} />
				</div>
			</div>
		);
	}
}
export default App;

