import React, { Component } from 'react';
import './Tweet.css';
import retweetIcon from './images/retweet.png';

function TweetByContent(props) {
	const formatDate = (date) => {
		return `${date.split(' ').slice(1, 3).join(' ')} ${date.slice(-4)}`;
	};

	const { tweets: tw } = props;
	let renderMediaEl, src1, src2, src3, src4;

	if (tw.extended_entities) {
		let media = tw.extended_entities.media[0];
		if (media.type === 'photo') {
			renderMediaEl = <img src={media.media_url} width="300" />;
		} else if (media.type === 'video') {
			if (media.video_info.variants[0]) src1 = media.video_info.variants[0].url;
			if (media.video_info.variants[1]) src2 = media.video_info.variants[1].url;
			if (media.video_info.variants[2]) src3 = media.video_info.variants[2].url;
			if (media.video_info.variants[3]) src4 = media.video_info.variants[3].url;
			renderMediaEl = (
				<video src={src1} src={src2} src={src3} src={src3} width="320" height="240" controls="controls" />
			);
		}
	}
	return (
		<div>
			<div className="tweet__container">
				<div className="tweet__header">
					<img className="tweet__avatar" src={tw.user.profile_image_url} alt="avatar" />
					<div>
						<p className="tweet__username">
							{tw.user.name} <span className="tweet__username screen-name">@{tw.user.screen_name}</span>
						</p>
					</div>
				</div>
				<div className="tweet__text">
					<p>{tw.full_text}</p>
					{renderMediaEl}
				</div>
				<div className="tweet__stats">
					<p className="tweet__stats-likes"> ❤️ {tw.favorite_count}</p>
					<div className="tweet__stats-retweet">
						<img src={retweetIcon} alt="retweet" />
						<span> {tw.retweet_count}</span>
					</div>
				</div>
				<p className="tweet__date">{formatDate(tw.created_at)}</p>
			</div>
		</div>
	);
}
export default TweetByContent;
