import React, { Component } from 'react';
import NavBar from './NavBar';
import Routes from './Routes';
import './App.css';
import axios from 'axios';

class App extends Component {
	state = {
		tweets: [
			{
				username: 'Emin Muratoglu',
				text:
					'Ssumenda quia animi mollitia laboriosam non ea eveniet ducimus repellat totam dignissimos corporis alias, veniam sapiente dolor eius maiores veritatis hic beatae. ',
				date: '10-05-20',
				avatar_src: '',
				likes: 15,
				retweets: 8
			}
		]
	};

	componentDidMount() {
		this.getData();
	}

	getData = async () => {
		const tweetsArr = [];
		const res = await axios.get('/api/tweets');
		console.log(res.data);
		for (let twt of res.data) {
			let fav_count = twt.favorite_count.split(' ').slice(0, 3).join(' ');
			tweetsArr.push({
				date: twt.created_at,
				favorite_count: fav_count
			});
		}
		console.log(tweetsArr);
	};

	render() {
		return (
			<div className="App">
				<NavBar />
				<div className="content__container">
					<h1 className="display-3">Twitter Showcase App</h1>
					<Routes tweets={this.state.tweets} />
				</div>
			</div>
		);
	}
}
export default App;
