import React, { Component } from 'react';
// import searchPicture from './images/search-picture.jpg';
import searchPicturePng from './images/search-picture.png';

class SearchTweets extends Component {
	render() {
		return (
			<div className="search__container">
				<div>
					<img src={searchPicturePng} className="search__icon" style={{ width: '8rem', marginBottom: '-1rem' }} />
					<p>Search for tweets either by a user or a topic</p>
				</div>
				<form className="form-group d-flex gap-1 my-lg-5 my-sm-1">
					<input className="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search" />
					<button className="btn btn-primary my-2 my-sm-0" type="button" style={{ whiteSpace: 'nowrap' }}>
						Search By User
					</button>
					<button className="btn btn-primary my-2 my-sm-0" type="button" style={{ whiteSpace: 'nowrap' }}>
						Search By Subject
					</button>
				</form>
			</div>
		);
	}
}

export default SearchTweets;
